function signOut(){firebase.auth().signOut().then(function(){alert("You have successfully logged out."),document.getElementById("login").style.display="initial",document.getElementById("logout").style.display="none",document.getElementById("homeLogo").dispatchEvent(new Event("click"))},function(b){console.error("Sign Out Error",b)})}var user=firebase.auth().currentUser;if(user){document.getElementById("login").style.display="none",document.getElementById("logout").style.display="initial";const userGreetingTag=document.getElementById("userGreeting");userGreetingTag.innerHTML="Signed in as "+user.email}else{document.getElementById("login").style.display="initial",document.getElementById("logout").style.display="none"}firebase.auth().onAuthStateChanged(function(d){if(d){document.getElementById("login").style.display="none",document.getElementById("logout").style.display="initial";const c=document.getElementById("userGreeting");c.innerHTML="Signed in as "+d.email}else{document.getElementById("login").style.display="initial",document.getElementById("logout").style.display="none"}});